1; t--){ //loop through all sub menus again, and use "display:none" to hide menus (to prevent possible page scrollbars  ultags[t].style.visibility="visible"  ultags[t].style.display="none"  }  }}if (window.addEventListener)window.addEventListener("load", initsidebarmenu, false)else if (window.attachEvent)window.attachEvent("onload", initsidebarmenu)</script><link rel="SHORTCUT ICON" href="images/favicon2.gif"></head><body ><div id="mainbody">  <div id="topheader"><img src="images/VQlogo.gif" title="Automated Operation Requests" alt="Automated Operation Requests" border="0" />    <ul>	<!--      <li class="alltkts"><a href="AllRequests.php"></a></li>      <li class="savedtkts"><a href="#"></a></li>      <li class="pendingtkts"><a href="#"></a></li>      <li class="tktstatus"><a href="#"></a></li>      <li class="logout"><a href="logout.php"></a></li> 148DC1	  -->	  <li class="projname"><center><h1 valign="top"><U><B>VQ Tool</B></U></h1></center></li>     </ul>  </div></div></body></html>